          <span :class="['menu-icon', item.iconClass]"></span>
        </div>
      </div>
    </div>
  </template>
  
  <script>
  export default {
    name: 'FloatingMenu',
    data() {
      return {
        isOpen: false,
        items: [
          { iconClass: 'fa fa-home', action: () => alert('Home clicked') },
          { iconClass: 'fa fa-user', action: () => alert('Profile clicked') },
          { iconClass: 'fa fa-cog', action: () => alert('Settings clicked') }
        ]
      };
    },
    computed: {
      menuStyle() {
        return {
          bottom: '20px',
          right: '20px',
        };
      }
    },
    methods: {
      toggleMenu() {
        this.isOpen = !this.isOpen;
      }
    }
  };
  </script>
  
